mapper.select:
SELECT COUNT(*)
FROM collection_likes
WHERE user_id=#{userId} AND collection_id=#{collectionId}

mapper.delete:
DELETE
FROM collection_likes
WHERE user_id=#{userId} AND collection_id=#{collectionId}

mapper.select:
SELECT COUNT(*)
FROM collection_likes
WHERE collection_id=#{collectionId}

SELECT collection_id
FROM collection_likes
WHERE user_id=#{userId}
