mapper.delete:
DELETE
FROM collections
WHERE collection_id=#{collectionId}
AND user_id=#{userId}

mapper.select:
SELECT collection_id, user_id, title, description, scope, created_at, music_keyword, image_path
FROM collections
WHERE collection_id=#{collectionId}

SELECT collection_id, user_id, title, description, scope, created_at, music_keyword, image_path
FROM collections
WHERE user_id=#{userId}
