mapper.delete:
DELETE
FROM playlists
WHERE playlist_id=#{playlistId}
AND user_id=#{userId}

mapper.select:
SELECT playlist_id, user_id, title, description, scope, created_at, music_keyword, image_path
FROM playlists
WHERE playlist_id=#{playlistId}

SELECT playlist_id, user_id, title, description, scope, created_at, music_keyword, image_path
FROM playlists
WHERE user_id=#{userId}
