mapper.select:
SELECT password
FROM users
WHERE email = #{email}

SELECT *
FROM users

    WHERE email = #{value}


    WHERE nickname = #{value}

mapper.update:
UPDATE users
password = #{password}, nickname = #{nickname}, introduction = #{introduction},
WHERE user_id = #{userId}

UPDATE users
SET password = #{password}
WHERE email = #{email}

UPDATE users
SET user_image = #{userImage}
WHERE user_id = #{userId}

mapper.select:
